               tables[tt].columns.forEach(function(c){
                   var type = /int\d+/.test(c.type) ? typeMap['int'] : typeMap[c.type];
                   var props = [
                   'name : \''+c.field+'\'',
                   'label : \''+c.field+'\'.humanize()',
                   'type : \''+type+'\'',
                   'required : '+(!c.not_null?'false':'true')
