                   if(type === typeMap['int'] && c.field === 'id')
                       props.push('primaryKey : true');
                   if(c.length_var!==-1)
                       props.push('max : '+(config.main.db.driver==='postgres'?c.length_var-4:c.length_var)); // postgres adds 4 chars

